using UnityEngine;using System.Collections;public class ShieldFlickerer : MonoBehaviour {    [SerializeField] GameObject Player;    [SerializeField] SpriteRenderer ShieldRenderer;    [SerializeField] float rate;    bool FlickerState = false;    int tick = 0;	// Use this for initialization	void Start () {		}		// Update is called once per frame	void Update () {        if (Player.GetComponent<PlayerController_Spaceship>().Shielded == true)        {            tick = tick + 1;            if (tick >= rate && FlickerState == true) {                tick = 0;                ShieldRenderer.color = new Color(128, 255, 255, 48);                FlickerState = false;            }            else if (tick >= rate && FlickerState == false) {                tick = 0;                ShieldRenderer.color = new Color(255, 255, 255, 255);                FlickerState = true;            }        }	}}